INSERT INTO `wp_posts` VALUES (242, 1, '2010-05-10 13:00:35', '2010-05-11 12:00:43', 'Begin 2010 vond de voorlopige oplevering plaats van het grootschalige bouwproject Forum in opdracht van Immobel.\r\nDit is de eerste fase van een project van  +/- 50.000 m² met kantoren en 32 appartementen op de hoek van de Ijzerenkruisstraat met de Parlementstraat.In dit gebouw komen de diensten van het Federaal Parlement. De tweede fase van de werken start in de tweede helft van 2010.', 'Forum', '', 'publish', 'closed', 'closed', '', 'forum-nl', '', '', '2012-12-14 19:05:58', '2012-12-14 18:05:58', '', 0, 'http://localhost:8888/archi2000/wp/?post_type=news&#038;p=242', 0, 'news', '', 0); 
